import ReactDOM from "https://esm.sh/react-dom@18.2.0";

// Inject Tailwind
const style = document.createElement("link");
style.href =
   "https://cdn.jsdelivr.net/npm/tailwindcss@2.2.19/dist/tailwind.min.css";
style.rel = "stylesheet";
document.head.appendChild(style);

function PoliceTowForm({ rowData, towReasons, towCompanies }) {
   const [row, setRow] = React.useState({});

   React.useEffect(() => {
      if (appsmith.model.rowData) {
         setRow(appsmith.model.rowData); // edit mode
      } else {
         setRow({}); // add mode
      }
   }, [appsmith.model.rowData]);

   function bindInput(field) {
      return {
         value: appsmith.model?.[field] ?? "",
         onChange: (e) => {
            const newRow = {
               ...row,
               [field]: e.target.value,
            };
            setRow(newRow);
            appsmith.updateModel(newRow);
         },
      };
   }

   return (
      <div className="p-6 bg-white rounded-2xl w-full max-w-6xl mx-auto">
         <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold"> Add / Edit Police Tow </h2>
            <button
               className="bg-red-500 hover:bg-red-600 text-white font-semibold px-8 py-2 rounded-lg flex items-center"
               onClick={(e) => {
                  console.log(appsmith.model);
               }}
            >
               <span className="mr-2"> âš  </span> SVS
            </button>
         </div>
         <div className="grid grid-cols-2 gap-6">
            {/* Left side fields */}
            <div>
               {/* Date/Time and Hold */}
               <div className="flex items-center mb-4 space-x-4">
                  <div className="flex space-x-4 mb-4">
                     {/* Date (MM-DD-YYYY) */}
                     <div className="flex flex-col flex-1">
                        <label className="text-sm font-medium mb-1">Date</label>
                        <input
                           type="text"
                           placeholder="MM-DD-YYYY"
                           className="border rounded-md p-2 w-full"
                           value={appsmith?.model?.date ?? ""}
                           onChange={(e) => {
                              const newRow = { ...row, date: e.target.value };
                              setRow(newRow);
                              appsmith.updateModel(newRow);
                           }}
                        />
                     </div>

                     {/* Time (HHMM) */}
                     <div className="flex flex-col flex-1">
                        <label className="text-sm font-medium mb-1">Time</label>
                        <input
                           type="text"
                           maxLength={4}
                           placeholder="2359"
                           className="border rounded-md p-2 w-full"
                           value={appsmith?.model?.time ?? ""}
                           onChange={(e) => {
                              const raw = e.target.value
                                 .replace(/\D/g, "")
                                 .slice(0, 4); // enforce 4 digits
                              const newRow = { ...row, time: raw };
                              setRow(newRow);
                              appsmith.updateModel(newRow);
                           }}
                        />
                     </div>
                  </div>

                  <div className="flex items-center space-x-2 mt-6">
                     <label className="text-sm font-medium">HOLD</label>
                     <input
                        type="checkbox"
                        className="w-5 h-5"
                        checked={!row.hold}
                        onChange={(e) => {
                           const newRow = { ...row, hold: e.target.checked };
                           setRow(newRow);
                           appsmith.updateModel(newRow);
                        }}
                     />
                  </div>
               </div>
               {/* Tow Reason */}
               <div className="mb-4">
                  <label className="text-sm font-medium mb-1">Tow Reason</label>

                  <select
                     className="border rounded-md p-2 w-full"
                     value={String(
                        appsmith?.model?.towreasonid ??
                           appsmith?.model?.towreason_id ??
                           ""
                     )}
                     onChange={(e) => {
                        const id = e.target.value;

                        if (id === "Other") {
                           const customReason = prompt(
                              "Enter custom tow reason:"
                           );
                           if (customReason) {
                              appsmith.triggerEvent("insertCustomReason", {
                                 reason: customReason,
                              });
                           }
                           return;
                        }

                        const list = appsmith.model.towReasons || [];
                        const sel = list.find(
                           (r) =>
                              String(
                                 r.id ?? r.reason_id ?? r.code ?? r.reason
                              ) === String(id)
                        );

                        const newRow = {
                           ...row,
                           towreasonid: id,
                           towreason_id: id,
                           towreason:
                              sel?.reason ?? sel?.label ?? sel?.code ?? "",
                           towreasondesc: sel?.description ?? sel?.notes ?? "",
                        };

                        setRow(newRow);
                        appsmith.updateModel(newRow);
                     }}
                  >
                     <option value="" disabled hidden>
                        Select a reason
                     </option>
                     {(appsmith.model.towReasons || []).map((r) => {
                        const optId = String(
                           r.id ?? r.reason_id ?? r.code ?? r.reason
                        );
                        const label = r.reason ?? r.label ?? r.code ?? optId;
                        return (
                           <option key={optId} value={optId}>
                              {label}
                           </option>
                        );
                     })}
                     <option value="Other">Other</option>
                  </select>
               </div>
               {/* Tow Company */}
               <div className="mb-4">
                  <label className="text-sm font-medium mb-1">
                     Tow Company
                  </label>
                  <select
                     className="border rounded-md p-2 w-full"
                     value={appsmith?.model?.towcompany_id ?? ""}
                     onChange={(e) => {
                        const id = e.target.value;
                        const list = appsmith.model.towCompanies || [];
                        const sel = list.find(
                           (c) => String(c.id ?? c.company_id) === String(id)
                        );

                        const newRow = {
                           ...row,
                           towcompany_id: id,
                           // denormalized helpers (optional, handy for print/notes)
                           towcompanyname: sel?.name ?? sel?.company ?? "",
                           towcompanyphone: sel?.phone ?? "",
                           towcompanyaddress: sel?.address ?? "",
                        };

                        setRow(newRow);
                        appsmith.updateModel(newRow);
                     }}
                  >
                     <option value="" disabled hidden>
                        Select a company
                     </option>
                     {(appsmith.model.towCompanies || []).map((c) => {
                        const optId = String(c.id ?? c.company_id);
                        const label = c.name ?? c.company;
                        return (
                           <option key={optId} value={optId}>
                              {label}
                           </option>
                        );
                     })}
                  </select>
               </div>
               <div className="bg-gray-200 text-black font-bold p-2 rounded mb-4">
                  TOW COMPANY INFO TOW COMPANY INFO TOW COMPANY INFO TOW COMPANY
                  INFO
               </div>
               {/* Vehicle Info */}
               <div className="grid grid-cols-4 gap-4 mb-4">
                  <div>
                     <label className="text-sm font-medium mb-1">Make</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("make")}
                     />
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">Model</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("model")}
                     />
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">Year</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("vehicleyear")}
                     />
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">Color</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("color")}
                     />
                  </div>
               </div>
               <div className="grid grid-cols-3 gap-4 mb-4">
                  <div>
                     <label className="text-sm font-medium mb-1">
                        License Plate
                     </label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("licenseplate")}
                     />
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">State</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("state")}
                     />
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">VIN</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("vin")}
                     />
                  </div>
               </div>
            </div>
            {/* Notes on the right */}
            <div className="flex flex-col h-full">
               <label className="text-sm font-medium mb-1"> Notes </label>
               <textarea
                  className="border rounded-md p-2 w-full h-full min-h-[300px]"
                  {...bindInput("notes")}
                  readOnly
               ></textarea>
            </div>
         </div>
         {/* Submit */}
         <div className="flex justify-end mt-4">
            <button 
							className="bg-blue-600 hover:bg-purple-700 text-white font-semibold px-6 py-2 rounded-lg"
							onClick={() => (row.id
								? UpdatePoliceTow.run(() => GetPoliceTows.run())
      					: AddPoliceTow.run(() => GetPoliceTows.run()))}>
               Save
            </button>
         </div>
      </div>
   );
}

