  height: calc(var(--appsmith-ui-height) * 1px);
  width: calc(var(--appsmith-ui-width) * 1px);
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  box-shadow: var(--appsmith-theme-boxShadow);
  padding: 29px 25px;
