  font-family: system-ui;
  background: #fff;
}

.tip-container {
  margin-bottom: 20px;
  font-size: 14px;
  line-height: 1.571429;
}

.tip-container h2 {
  margin-bottom: 20px;
  font-size: 16px;
  font-weight: 700;
}

.tip-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  margin-bottom: 9px;
}

.tip-header div {
  color: #999;
}

.content {
  white-space: pre-wrap;
  word-break: break-word;
  font-size: 14px;
  line-height: 1.571429;
}

.button-container {
  text-align: right;
  padding-top: 4px;
}

.button-container button {
  margin: 0 10px;
  cursor: pointer;
  border-radius: var(--appsmith-theme-borderRadius);
  padding: 6px 16px;
  background: none;
  height: auto;
  transition: all 0.3s ease;
}

.button-container button.primary {
  background: var(--appsmith-theme-primaryColor) !important;
  color: #fff;
  border: 1px solid var(--appsmith-theme-primaryColor) !important;
}

.button-container button.reset {
  border: 1px solid #999;
  color: #999;
  outline: none;
  box-shadow: none;
}

.button-container button.reset:hover:not(:disabled) {
  color: var(--appsmith-theme-primaryColor);
  border-color: var(--appsmith-theme-primaryColor);
}

.button-container button.reset:disabled {
  cursor: default;
}

