import React from "https://esm.sh/react@18.2.0";
import ReactDOM from "https://esm.sh/react-dom@18.2.0";

// Inject Tailwind
const style = document.createElement("link");
style.href =
   "https://cdn.jsdelivr.net/npm/tailwindcss@2.2.19/dist/tailwind.min.css";
style.rel = "stylesheet";
document.head.appendChild(style);

function PoliceTowForm({ rowData, towReasons, towCompanies }) {
   const [row, setRow] = React.useState({});

   const makesRaw = appsmith.model.makes || {};
   const makes = Array.isArray(makesRaw)
      ? makesRaw
      : Object.values(makesRaw).flat();

   React.useEffect(() => {
      console.log(appsmith.model);
      if (appsmith.model.rowData) {
         setRow(appsmith.model.rowData);
      } else {
         setRow({});
      }
   }, [appsmith.model.rowData]);

   React.useEffect(() => {
      if (Object.keys(row).length === 0) {
         setRow(appsmith.model.rowData || appsmith.model || {});
      }
   }, [appsmith.model]);

   function bindInput(field) {
      return {
         value: appsmith?.model?.[field] ?? "",
         onChange: (e) => {
            const newRow = {
               ...row,
               [field]: e.target.value,
            };
            setRow(newRow);
            appsmith.updateModel(newRow);
         },
      };
   }

   return (
      <div className="p-6 bg-white rounded-2xl w-full max-w-6xl mx-auto">
         <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold">
               {row?.id ? "EDIT POLICE TOW" : "ADD POLICE TOW"}
            </h2>

            <button
               className="bg-red-500 hover:bg-red-600 text-white font-semibold px-8 py-2 rounded-lg flex items-center"
               onClick={(e) => {
                  console.log(appsmith.model);
               }}
            >
               <span className="mr-2"> âš  </span> SVS
            </button>
         </div>
         <div className="grid grid-cols-2 gap-6">
            {/* Left side fields */}
