               {/* Date/Time/DR and Hold */}
               <div className="flex items-center mb-4 space-x-4">
                  <div className="flex space-x-4 mb-4">
                     {/* Date (MM-DD-YYYY) */}
                     <div className="flex flex-col flex-none w-1/3">
                        <label className="text-sm font-medium mb-1">Date</label>
                        <input
                           type="text"
                           placeholder="MM-DD-YYYY"
                           className="border rounded-md p-2 w-full font-mono tracking-wider"
                           value={row.date ?? ""}
                           onChange={(e) => {
                              // Remove non-digits
                              const digits = e.target.value
                                 .replace(/\D/g, "")
                                 .slice(0, 8);

                              // Auto-insert dashes
                              let formatted = digits;
                              if (digits.length > 4) {
                                 formatted = `${digits.slice(
                                    0,
                                    2
                                 )}-${digits.slice(2, 4)}-${digits.slice(4)}`;
                              } else if (digits.length > 2) {
                                 formatted = `${digits.slice(
                                    0,
                                    2
                                 )}-${digits.slice(2)}`;
                              }

                              const newRow = { ...row, date: formatted };
                              setRow(newRow);
                              appsmith.updateModel(newRow);
                           }}
                           onBlur={(e) => {
                              // Optional validation: ensure full 8 digits
                              if (!/^\d{2}-\d{2}-\d{4}$/.test(e.target.value)) {
                                 alert(
                                    "Please enter a valid date (MM-DD-YYYY)"
                                 );
                              }
                           }}
                        />
                     </div>

                     {/* Time (HHMM) */}
                     <div className="flex flex-col w-1/5">
                        <label className="text-sm font-medium mb-1">Time</label>
                        <input
                           type="text"
                           inputMode="numeric"
                           maxLength={4}
                           placeholder="2359"
                           className="border rounded-md p-2 w-full text-center"
                           value={appsmith?.model?.time ?? ""}
                           onChange={(e) => {
                              const raw = e.target.value
                                 .replace(/\D/g, "")
                                 .slice(0, 4);
                              const newRow = { ...row, time: raw };
                              setRow(newRow);
                              appsmith.updateModel(newRow);
                           }}
                        />
                     </div>

                     {/* DR Number */}
                     <div className="flex flex-col w-1/3">
                        <label className="text-sm font-medium mb-1">DR#</label>
                        <input
