                           onChange={(e) => {
                              const raw = e.target.value
                                 .replace(/\D/g, "")
                                 .slice(0, 8);
                              const newRow = { ...row, dr: raw };
                              setRow(newRow);
                              appsmith.updateModel(newRow);
                           }}
                        />
                     </div>
                  </div>

                  <div className="flex items-center space-x-2 mt-6">
                     <label className="text-sm font-medium">HOLD</label>
                     <input
                        type="checkbox"
                        className="w-5 h-5"
                        checked={!!row.hold}
                        onChange={(e) => {
                           const newRow = { ...row, hold: e.target.checked };
                           setRow(newRow);
                           appsmith.updateModel(newRow);
                        }}
                     />
                  </div>
               </div>

               {/* Tow Reason */}
               <div className="mb-4">
                  <label className="text-sm font-medium mb-1">Tow Reason</label>

                  <select
                     className="border rounded-md p-2 w-full"
                     value={String(
                        appsmith?.model?.towreasonid ??
                           appsmith?.model?.towreason_id ??
                           ""
                     )}
                     onChange={(e) => {
                        const id = e.target.value;

                        if (id === "Other") {
                           const customReason = prompt(
                              "Enter custom tow reason:"
                           );
                           if (customReason) {
                              appsmith.triggerEvent("insertCustomReason", {
                                 reason: customReason,
                              });
                           }
                           return;
                        }

                        const list = appsmith.model.towReasons || [];
                        const sel = list.find(
                           (r) =>
                              String(
                                 r.id ?? r.reason_id ?? r.code ?? r.reason
                              ) === String(id)
                        );

                        const newRow = {
                           ...row,
                           towreasonid: id,
                           towreason_id: id,
                           towreason:
                              sel?.reason ?? sel?.label ?? sel?.code ?? "",
                           towreasondesc: sel?.description ?? sel?.notes ?? "",
                        };

                        setRow(newRow);
                        appsmith.updateModel(newRow);
                     }}
                  >
                     <option value="" disabled hidden>
                        Select a reason
                     </option>
                     {(appsmith.model.towReasons || []).map((r) => {
                        const optId = String(
                           r.id ?? r.reason_id ?? r.code ?? r.reason
                        );
                        const label = r.reason ?? r.label ?? r.code ?? optId;
                        return (
                           <option key={optId} value={optId}>
                              {label}
                           </option>
                        );
                     })}
                     <option value="Other">Other</option>
                  </select>
               </div>
               {/* Tow Company */}
               <div className="mb-4">
                  <label className="text-sm font-medium mb-1">
                     Tow Company
                  </label>
                  <select
                     className="border rounded-md p-2 w-full"
                     value={appsmith?.model?.towcompany_id ?? ""}
                     onChange={(e) => {
                        const id = e.target.value;
                        const list = appsmith.model.towCompanies || [];
                        const sel = list.find(
                           (c) => String(c.id ?? c.company_id) === String(id)
                        );

                        const newRow = {
                           ...row,
                           towcompany_id: id,
                           // denormalized helpers (optional, handy for print/notes)
                           towcompanyname: sel?.name ?? sel?.company ?? "",
                           towcompanyphone: sel?.phone ?? "",
                           towcompanyaddress: sel?.address ?? "",
                        };

                        setRow(newRow);
                        appsmith.updateModel(newRow);
                     }}
                  >
                     <option value="" disabled hidden>
                        Select a company
                     </option>
                     {(appsmith.model.towCompanies || []).map((c) => {
                        const optId = String(c.id ?? c.company_id);
                        const label = c.name ?? c.company;
                        return (
                           <option key={optId} value={optId}>
                              {label}
                           </option>
                        );
                     })}
                  </select>
               </div>
               <div className="bg-gray-200 text-black font-bold p-2 rounded mb-4">
                  TOW COMPANY INFO TOW COMPANY INFO TOW COMPANY INFO TOW COMPANY
                  INFO
               </div>
               {/* Vehicle Info */}
               <div className="grid grid-cols-4 gap-4 mb-4">
                  <div>
                     <label className="text-sm font-medium mb-1">Make</label>
                     <select
                        className="border rounded-md p-2 w-full"
                        value={appsmith.model.make_id || ""}
                        onChange={(e) => {
                           const id = Number(e.target.value);
                           const list = appsmith.model.makes || [];
                           const sel = list.find((m) => m.id === id);

                           const newRow = {
                              ...row,
                              make_id: id,
                              make_name: sel?.make || null,
                           };
                           setRow(newRow);
                           appsmith.updateModel(newRow);
                        }}
                     >
                        <option value="" disabled hidden>
                           Select
                        </option>
                        {(appsmith.model.makes || []).map((m) => (
                           <option key={m.id} value={m.id}>
                              {m.make}
                           </option>
                        ))}
                     </select>
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">Model</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("model")}
                     />
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">Year</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("vehicleyear")}
                     />
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">Color</label>
                     <select
                        className="border rounded-md p-2 w-full"
                        value={String(appsmith.model.color_id || "")}
                        onChange={(e) => {
                           const id = Number(e.target.value);
                           const list = appsmith.model.colors || [];
                           const sel = list.find((c) => c.id === id);

                           const newRow = {
                              ...row,
                              color_id: id,
                              color_name: sel?.color || null, // optional denormalized helper
                           };

                           setRow(newRow);
                           appsmith.updateModel(newRow);
                        }}
                     >
                        <option value="" disabled hidden>
                           Select
                        </option>
                        {(appsmith.model.colors || []).map((c) => (
                           <option key={c.id} value={c.id}>
                              {c.color}
                           </option>
                        ))}
                     </select>
                  </div>
               </div>
               <div className="grid grid-cols-3 gap-4 mb-4">
                  <div>
                     <label className="text-sm font-medium mb-1">
                        License Plate
                     </label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("licenseplate")}
                     />
                  </div>
                  <div>
                     <label className="text-sm font-medium mb-1">State</label>
                     <select
                        className="border rounded-md p-2 w-full"
                        value={String(appsmith.model.licensestate_id || "")}
                        onChange={(e) => {
                           const id = Number(e.target.value);
                           const list = appsmith.model.states || [];
                           const sel = list.find((s) => s.id === id);

                           const newRow = {
                              ...row,
                              licensestate_id: id,
                              state_name: sel?.state || null, // optional denormalized helper
                           };

                           setRow(newRow);
                           appsmith.updateModel(newRow);
                        }}
                     >
                        <option value="" disabled hidden>
                           Select
                        </option>
                        {(appsmith.model.states || []).map((s) => (
                           <option key={s.id} value={s.id}>
                              {s.state}
                           </option>
                        ))}
                     </select>
                  </div>

                  <div>
                     <label className="text-sm font-medium mb-1">VIN</label>
                     <input
                        type="text"
                        className="border rounded-md p-2 w-full"
                        {...bindInput("vin")}
                     />
                  </div>
               </div>
            </div>
            <div className="flex flex-col h-full">
               {/* Released Checkbox */}
               <div className="flex items-center mb-3">
                  <label className="text-sm font-medium mr-2">Released</label>
                  <input
                     type="checkbox"
                     className="w-5 h-5"
                     checked={!!row.released}
                     onChange={(e) => {
                        const newRow = { ...row, released: e.target.checked };
                        setRow(newRow);
                        appsmith.updateModel(newRow);
                     }}
                  />
               </div>

               {/* Notes */}
               <label className="text-sm font-medium mb-1"> Notes </label>
               <textarea
                  className="border rounded-md p-2 w-full h-full min-h-[300px]"
                  {...bindInput("notes")}
               ></textarea>
            </div>
         </div>
         {/* Submit */}
         <div className="flex justify-end mt-4">
            <button
               className="bg-blue-600 hover:bg-purple-700 text-white font-semibold px-6 py-2 rounded-lg"
               onClick={() => {
                  const action = row.id ? "onUpdateTow" : "onAddTow";
                  appsmith.triggerEvent(action, { row });
               }}
            >
               Save
            </button>
         </div>
      </div>
   );
}

appsmith.onReady(() => {
   ReactDOM.render(
      <PoliceTowForm
         rowData={appsmith.model.rowData}
         towReasons={appsmith.model.towReasons}
         towCompanies={appsmith.model.towCompanies}
      />,
      document.getElementById("root")
   );
});
