import ReactDOM from "https://esm.sh/react-dom@18.2.0";

function CustomTable({ data, triggerEvent }) {
  return (
    <table style={{ width: "100%", borderCollapse: "collapse", fontFamily: "Arial, sans-serif" }}>
      <thead>
        <tr style={{ backgroundColor: "#2c3e50", color: "white", textAlign: "left" }}>
          <th style={{ padding: "8px" }}>Plate</th>
          <th style={{ padding: "8px" }}>Make</th>
          <th style={{ padding: "8px" }}>Model</th>
          <th style={{ padding: "8px" }}>Color</th>
          <th style={{ padding: "8px" }}>Date</th>
          <th style={{ padding: "8px" }}>Time</th>
        </tr>
      </thead>
      <tbody>
        {Array.isArray(data) && data.map((row, i) => (
          <tr
  key={i}
  style={{ cursor: "pointer", backgroundColor: i % 2 === 0 ? "#f9f9f9" : "white" }}
  onClick={() => {
    appsmith.updateModel({ selectedRow: row });
    triggerEvent("onRowClick", row);
  }}
>
  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{row.licenseplate}</td>
  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{row.make}</td>
  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{row.model}</td>
  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{row.color}</td>
  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{row.date}</td>
  <td style={{ padding: "8px", borderBottom: "1px solid #ddd" }}>{row.time}</td>
</tr>

        ))}
      </tbody>
    </table>
  );
}

appsmith.onReady(() => {
  const root = document.getElementById("root");
  ReactDOM.render(
    <CustomTable data={appsmith.model.data} triggerEvent={appsmith.triggerEvent} />,
    root
  );
});
